INSERT INTO `component` (`comid`, `name`, `text`, `filename`) VALUES
(1, 'formCallback', '<!-- Форма заказа -->\r\n        <div class=\"col-md-6\">\r\n            <div class=\"panel panel-default\">\r\n                <div class=\"panel-heading\">\r\n                    <h3 class=\"panel-title\">Форма заявки на ремонт</h3>\r\n                </div>\r\n                <div class=\"panel-body\">	\r\n                    <form id=\"callback\" action=\"/ajax/callBack.php\">\r\n                        <div class=\"form-group\">\r\n                            <label  for=\"inputEmail3\" class=\"col-sm-3 control-label\">Ваше имя</label>\r\n                            <div  class=\"col-sm-9\">\r\n                                <input class=\"form-control\" id=\"callBack_name\" type=\"text\" name=\"input_text_1\" placeholder=\"Введите Ваше имя\"/>\r\n                                <span id=\"error-message-input_text_1\"></span>\r\n                                <br />\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"form-group\">\r\n                            <label for=\"inputEmail3\" class=\"col-sm-3 control-label\">Телефон</label>\r\n                            <div class=\"col-sm-9\">\r\n                                <input class=\"form-control\" id=\"callBack_telephone\" type=\"text\" name=\"input_text_2\" placeholder=\"Введите номер телефона\"/>\r\n                                <span id=\"error-message-input_text_2\"></span>\r\n                                <br />\r\n                            </div>\r\n                        </div>\r\n                        <div class=\"form-group\">\r\n                            <label for=\"inputEmail3\" class=\"col-sm-3 control-label\">Описание проблемы</label>\r\n                            <div class=\"col-sm-9\">\r\n                                <textarea class=\"form-control\" id=\"callBack_problem\" rows=\"3\" placeholder=\"Опишите проблему\"></textarea>\r\n                                <span id=\"error-message-input_textarea_problem\"></span>\r\n                                <br />\r\n                            </div>\r\n                        </div>\r\n                        <center><p>Нажмите кнопку отправить и мы свяжемся с Вами</p></center>\r\n                        <div class=\"form-group\">\r\n                            <button type=\"submit\" class=\"btn btn-success btn-block\">Отправить</button>\r\n                        </div>\r\n                    </form>\r\n                </div>\r\n            </div>				\r\n        </div> <!-- Форма заказа -->', ''),
(2, 'mainPageText', '<div class=\"col-md-6\">\r\n            <!-- Выгодные предложения -->\r\n            <h3>Выгодные предложения</h3>\r\n            <ul>\r\n                <li class=\"sales_li\">Чистка ноутбука от пыли + замена термопасты + смазка вентиляторов - <b>500 рублей</b></li>\r\n                <li class=\"sales_li\">Настройка маршрутизатора (роутера) wi-fi + выезд на дом - <b>500 рублей</b></li>\r\n                <li class=\"sales_li\">Установка и настройка антивируса - <b>250 рублей</b></li>\r\n                <li class=\"sales_li\">Установка и настройка операционной системы Windows + установка драйверов - <b>500 рублей</b></li>\r\n            </ul>\r\n            <!-- Приемущества -->\r\n            <h3>Наши принципы</h3>\r\n            <ul>\r\n                <li class=\"advant_li\">Выполняем работу <b>точно в</b> оговоренный с Вами <b>срок</b></li>\r\n                <li class=\"advant_li\">Проводим только те <b>работы, которые необходимы Вам</b> и которые были с Вами согласованы</li>\r\n                <li class=\"advant_li\">Осуществляем сотрудничество с оптово-розничными компьютерными компаниями, поэтому для наших клиентов <b>цены на комплектующие</b> при ремонте <b>не привышают рыночных</b></li>\r\n                <li class=\"advant_li\"><b>Стоимость выполняемых работ</b> в нашем сервисе <b>не превышает среднюю рыночную стоимость</b> аналогичных услуг среди фирм города Красноярска, а часто может оказаться и ниже</li>\r\n                <li class=\"advant_li\">Если случилось вдруг, что Вы обнаружили проблемы, которые мы не устранили или сделали не качественно, то <b>мы исправим</b> их <b>бесплатно</b></li>\r\n            </ul>\r\n            <br />\r\n        </div>', '');

